          }
          //阿里云效仓库，必须添加
          maven {
              credentials {
                  username '609cc5623a10edbf36da9615'
                  password 'EbkbzTNHRJ=P'
              }
              url 'https://packages.aliyun.com/maven/repository/2102846-release-8EVsoM/'
          }
      }
  }
  
  // 修改对应的sdk依赖，注意groupId已经变更，请整个依赖复制修改，请勿仅改动版本号
